mapper.sql:
  		FROM 
			bears.B_codegroup a
		WHERE 1 = 1

			AND ifcgDelNy = #{shDelNy}

			AND ifcgUseNy = #{shUseNy}


				AND a.ifcgRegist BETWEEN #{shDateStart} AND #{shDateEnd}
				AND a.ifcgEdit BETWEEN #{shDateStart} AND #{shDateEnd}



				AND ifcgSeq = #{shValue}
				AND ifcgName LIKE CONCAT('%',#{shValue},'%')

mapper.delete:
  		DELETE FROM B_codegroup
		WHERE 1 = 1
			AND ifcgSeq = #{ifcgSeq}
