mapper.select:
SELECT b.ifcdSeq, b.ifcdName, b.ifcdUseNy, b.ifcdOrder, b.ifcdDesc, b.ifcdDelNy, b.ifcdRegist, b.ifcdEdit, b.B_codegroup_ifcgSeq, a.ifcgName FROM bears.B_code AS b JOIN B_codegroup AS a ON a.ifcgSeq = b.B_codegroup_ifcgSeq WHERE 1 = 1 AND ifcdDelNy = #{shcdDelNy} AND ifcdUseNy = #{shcdUseNy}
AND b.ifcdRegist BETWEEN #{shcdDateStart} AND #{shcdDateEnd} AND b.ifcdEdit BETWEEN #{shcdDateStart} AND #{shcdDateEnd}
AND ifcdSeq = #{shcdValue} AND ifcdName LIKE CONCAT('%',#{shcdValue},'%') AND ifcgSeq = #{shcdValue} AND ifcgName LIKE CONCAT('%',#{shcdValue},'%')
ORDER BY b.ifcdSeq DESC

mapper.delete:
DELETE FROM bears.B_code
WHERE 1 = 1
	AND ifcdSeq = #{ifcdSeq}
