mapper.sql:
FROM
	bears.B_user a
WHERE 1 = 1
	AND usrDelNy = #{shDelNy}
	AND usrXdmNy = #{shXdmNy}
	AND usrTosNy = #{shTosNy}
	AND usrMktNy = #{shMktNy}

		AND usrGrade = 0
		AND usrGrade = 1
		AND usrGrade = 2
		AND usrGrade = 3
		AND usrGrade = 4


		AND usrRegDate BETWEEN #{shStartDate} AND #{shEndDate}
		AND usrEditdate BETWEEN #{shStartDate} AND #{shEndDate}


		AND usrSeq = #{shValue}
		AND usrName LIKE CONCAT('%',#{shValue},'%')
		AND usrId LIKE CONCAT('%',#{shValue},'%')
		AND usrGender LIKE CONCAT('%',#{shValue},'%')
		AND usrBirth LIKE CONCAT('%',#{shValue},'%')
		AND usrPhone LIKE CONCAT('%',#{shValue},'%')

mapper.delete:
DELETE FROM bears.B_user
WHERE 1 = 1
	AND usrSeq = #{usrSeq}
